css:
.col-6 {
  display: inline-block;
  width: 49.4%;
  margin-bottom: 6px;
  vertical-align: middle;
}

h3 {
  margin-bottom: 0;
}

.row {
  margin: 0;
}

.text-center {
  text-align: center;
}

.mt-0 {
  margin-top: 0;
}

.mb-0 {
  margin-bottom: 0;
}

.mb-20 {
    margin-bottom: 20px!important;
}

.mt-20 {
    margin-top: 20px!important;
}

.pt-12 {
    padding-top: 12px!important;
}

.bt-2 {
  border-top: 2px solid #000;
}

.bb-1 {
  border-bottom: 1px solid #a9b1b9;
}

.signature img {
    border: 1px solid #ececec;
}

html:
{{letter_head}}

<main>
  <div class="row bb-1">
    <div class="col-12">
      <p class="mt-20">{{doc.contract_terms}}</p>
    </div>
  </div>

  <div class="row">
      <p class="mb-20">By executing below or by electronic signature, the parties agree to accept the terms of this Agreement</p>
      </div>
       <div class="row">
    <div class="col-6 signature text-center">
      <p class="mb-0"><img class="pt-12" src="{{doc.signee_company}}" alt="Company Signature"></p>

      <p class="mt-0">
        {% if not doc.signed_by_company %}
            <br>
        {% else %}
           {{frappe.db.get_value("Employee", {"user_id": doc.signed_by_company}, "employee_name")}}<br>
            {% if not frappe.db.get_value("Employee", {"user_id": doc.signed_by_company}, "designation") %}
                <br>
            {% else %}
                {{ frappe.db.get_value("Employee", {"user_id": doc.signed_by_company}, "designation") }}
            {% endif %}
            {% if not frappe.db.get_value("Employee", {"user_id": doc.signed_by_company}, "company") %}

            {% else %}
                , {{ frappe.db.get_value("Employee", {"user_id": doc.signed_by_company}, "company") }}
            {% endif %}
        {% endif %}
      </p>

      <p class="mb-0">Date: {{ frappe.utils.formatdate(doc.creation, "dd MMMM, YYYY") }}</p>
    </div>
    {% if(doc.is_signed) %}
    <div class="col-6 signature text-center">
      {% if(doc.party_type != 'Employee') %}
      <p class="mb-0"><img src="{{doc.customer_signature}}" alt="Customer Signature"></p>
      <p class="mt-0">{{doc.signee}},<br> {{doc.party_name}}</p>
      <p class="mb-0">Date: {{ frappe.utils.formatdate(doc.signed_on, "dd MMMM, YYYY") }}</p>
    {% else %}
      <p class="mb-0"><img src="{{doc.customer_signature}}" alt="Customer Signature"></p>
      <p class="mt-0">{{doc.signee}}</p>
      <p class="mb-0">Date: {{ frappe.utils.formatdate(doc.signed_on, "dd MMMM, YYYY") }}</p>
    {% endif %}
    </div>
  </div>
  {% endif %}
</main>
