css:
.print-format div, .print-format span, .print-format td, .print-format h1, .print-format h2, .print-format h3, .print-format h4 {
    font-family: sans-serif
}

.wrapper {
    width:98%;
    margin: 0 auto;
}

.text-left {
    text-align: left;
}

.text-right {
    text-align: right;
}

.remittance {
    border-top: 3px dashed #efefef
}

table {
    width: 100%;
    table-layout: fixed;
}

table thead {
    border-bottom: 2px solid #000;
}

.remittance.no-padding {
    padding-bottom: 0;
}

.remittance.padding {
    padding-bottom: 32rem;
    padding-left: 0;
    padding-right: 0;
}

.bank-info {
    font-weight: bold;
    font-size: 20px;
    font-family: sans-serif;
    padding: 0px 6px 15px;
}

.remittance table thead td {
    font-weight: bold;
    padding: 0 6px!important;
    font-size: 13px;
}

.remittance table tbody {
    padding-top: 12px
}

.remittance table tbody td,
.remittance table tfoot td {
    padding: 6px 6px 0!important;
    font-size: 15px;
}

.remittance table tfoot td {
    font-weight: bold;
}
