css:
label,
.value {
  font-size: 12px;
  line-height: 20px;
}

.header-section,
.user-details-section,
.payment-details-section,
.terms-and-condition-section {
  padding: 20px 0;
}

.header-section h2 {
  color: #666666;
  font-size: 24px;
  font-weight: 500;
  text-transform: capitalize;
}

.header-section .license {
  border-bottom: 1px solid #d1d8dd;
  color: #777;
  font-size: 15.6px;
  font-weight: normal;
  padding-bottom: 10px;
}

.user-detail-section .supplier-name {
  font-size: 120%;
  font-weight: bold;
}

.table-section {
  margin: 15px 0px;
}

.payment-details-section .value,
.payment-template .value {
  padding: 0 15px;
  text-align: right;
}

.payment-details-section .in-word {
  text-align: justify;
}

.terms-and-condition-section {
  margin: 0px;
}

.terms-and-condition-section .terms {
  margin: 0px;
}

.terms-and-condition-section .terms .value p {
  text-align: justify;
}
