css:
.print-format {
    font-size: 10px;
    padding: 0.45in;
}

.table {
    margin: 0 0 2em 0;
    page-break-inside: avoid;
}

.table td {
    font-size: 0.9em;
    line-height: 1em !important;
}

.table-bordered > thead > tr > th,
.table-bordered > tbody > tr > td,
.table-bordered > tbody > tr > th {
    border: 1px solid black !important;
}

.manifest-header {
    font-size: 1.4em;
}

.manifest-disclaimer {
    font-size: 0.8em;
}

.section-header {
    font-size: 1em;
}

.section-subheader {
    font-size: 0.9em;
    font-weight: normal;
}

.table-left {
    padding-left: 0;
}

.table-right {
    padding-right: 0;
}
